sourcesContent:
import express from "express";
import morgan from "morgan";
import cors from "cors";
import "dotenv/config";

const app = express();

app.use(
  cors({
    origin: "*",
  })
);
app.use(express.json());
app.use(morgan("dev"));

try {
  app.all("*", notFound);
} catch (error) {
  /* eslint-disable-next-line no-console */
  console.log(error);
}

export default app;

